// Extract data from webhook
const { filename, content, analysis, timestamp } = $input.first().json;

// Log the incoming data
console.log('Received letter analysis:', {
  filename,
  contentLength: content?.length || 0,
  authenticityScore: analysis?.authenticity_score,
  riskLevel: analysis?.risk_assessment,
  timestamp
});

// Add metadata
const enrichedData = {
  ...$input.first().json,
  workflow_processed: true,
  processed_at: new Date().toISOString(),
  workflow_version: '1.0'
};

return enrichedData;

